VALUES ('CFBANK','Tra cứu nhanh thông tin ngân hàng','Bank information','SELECT format_custid(CF.CUSTID) CUSTID,
  case when CF.CUSTODYCD is null then '''' else format_custodycd(CF.CUSTODYCD) end CUSTODYCD,
      case when CF.CUSTODYCD is null then '''' else (CF.CUSTODYCD) end CUSTODYCD_SRCH,
  CF.SHORTNAME,CF.FULLNAME,CF.DATEOFBIRTH,CF.IDCODE,CF.IDDATE,CF.IDPLACE,CF.ADDRESS, mrloanlimit, A.cdcontent PROVINCE
FROM CFMAST CF, TLGROUPS GRP, ALLCODE A
WHERE CF.CAREBY = GRP.GRPID AND CF.Isbanking=''Y''
AND A.CDNAME =''PROVINCE'' and A.CDTYPE =''CF'' AND A.CDVAL = CF.PROVINCE
AND (SUBSTR(CF.CUSTID,1,4) = DECODE(''<$BRID>'', ''<$HO_BRID>'', SUBSTR(CF.CUSTID,1,4), ''<$BRID>'')
      OR CF.CAREBY IN (SELECT TLGRP.GRPID FROM TLGRPUSERS TLGRP WHERE TLID = ''<$TELLERID>'')
    )','CFBANK',null,null,null,0,50,'N',30,null,'Y','T');
COMMIT;
