VALUES ('CI1189','Revert 1132 do KH không rút tiền','Revert 1132 do KH không rút tiền','
SELECT  CI.TXDATE,CI.BUSDATE, CI.TXNUM, CI.ACCTNO, CI.BENEFBANK, CI.BENEFACCT,
       CI.BENEFCUSTNAME FULLNAME, CI.BENEFLICENSE IDCODE, CI.AMT, CI.FEEAMT,
       CI.DELTD, CI.BANKID, CI.BENEFIDDATE, CI.BENEFIDPLACE, CI.FEETYPE,
       CI.POTXNUM, CI.POTXDATE, CI.LAST_CHANGE, CI.RECEIVID, CI.RECEIVIDDATE,
       CI.RECEIVIDPLACE, CI.CITYEF, CI.CITYBANK, ''Chưa thực rút'' RMSTATUS,
       CF.CUSTODYCD, TO_DATE(SYS.VARVALUE,''DD/MM/RRRR'') - TO_DATE(CI.TXDATE,''DD/MM/RRRR'') DAYS
FROM CIREWITHDRAW CI, SYSVAR SYS, AFMAST AF, CFMAST CF
     WHERE CI.TLTXCD IN (''1132'',''1184'') AND CI.ACCTNO = AF.ACCTNO AND AF.CUSTID = CF.CUSTID AND
     RMSTATUS = ''P'' AND DELTD <> ''Y'' AND SYS.GRNAME=''SYSTEM'' AND SYS.VARNAME =''CURRDATE''
','CIMAST',null,null,'1189',null,50,'N',30,'NYNNYYYNNN','Y','T');
COMMIT;
