VALUES ('LN5540','Tra c?u tài kho?n vay tr? n?','View loan account for payment (waiting for 5540)','SELECT ADVPAY, NMLAMT, PRINODAMT,PRINNMLAMT,INTODAMT, INTNMLAMT,PRINNML ,PRINDUE ,PRINOVD,LOCATION,LNTYPE,DESC_LNTYPE ,MINTERM ,
