VALUES ('MR0006','Danh sách các lần SMS của khách hàng','View list SMS to customer','
SELECT * FROM (
SELECT a.autoid, a.actype,a.groupleader, a.acctno, a.email, a.mobile, a.address,
       a.fullname, a.phone1, a.desc_status, a.balance, a.bamt, a.odamt,
       a.advlimit, a.mrcrlimitmax, a.mrclamt, a.mrcrlimit, a.mrirate,
       a.mrmrate, a.mrlrate, a.marginrate, a.avlwithdraw, a.pp,
       a.avllimit, a.navaccount, a.outstanding, a.addvnd, a.smsstatus,
       a.execdate, a.exectime, a.calldate, a.calltime
       FROM SMSMARGINCALL a
UNION ALL
SELECT autoid, actype, groupleader,acctno, email, mobile, address,
       fullname, phone1, desc_status, balance, bamt, odamt,
       advlimit, mrcrlimitmax, mrclamt, mrcrlimit, mrirate,
       mrmrate, mrlrate, marginrate, avlwithdraw, pp,
       avllimit, navaccount, outstanding, addvnd, smsstatus,
       execdate, exectime, calldate, calltime
        FROM SMSMARGINPROCESSED) where 0=0
','SMSPROCESS',null,null,null,null,50,'N',30,'NYNNYYYNNN','Y','T');
COMMIT;
