DELETE SEARCH WHERE SEARCHCODE = 'MRTYPE';
INSERT INTO SEARCH (SEARCHCODE,SEARCHTITLE,EN_SEARCHTITLE,SEARCHCMDSQL,OBJNAME,FRMNAME,ORDERBYCMDSQL,TLTXCD,CNTRECORD,ROWPERPAGE,AUTOSEARCH,INTERVAL,AUTHCODE,ROWLIMIT,CMDTYPE)
VALUES ('MRTYPE','Loại hình ký quỹ tài khoản','MR product type','SELECT TYP.ACTYPE, SBC.CCYNAME CCYCD, TYP.TYPENAME, TYP.GLGRP, A0.CDCONTENT STATUS, TYP.GLBANK, TYP.SUBCD, TYP.DESCRIPTION NOTES,
A03.CDCONTENT APPRV_STS,
(CASE WHEN APPRV_STS IN (''D'') THEN ''N'' ELSE ''Y'' END) EDITALLOW, (CASE WHEN APPRV_STS IN (''P'') THEN ''Y'' ELSE ''N'' END) APRALLOW,
(CASE WHEN APPRV_STS IN (''D'') THEN ''N'' ELSE ''Y'' END) AS DELALLOW
FROM MRTYPE TYP, SBCURRENCY SBC, ALLCODE A0, ALLCODE A03
WHERE A0.CDTYPE = ''SY'' AND A0.CDNAME = ''STATUS'' AND A0.CDVAL=TYP.STATUS AND SBC.CCYCD=TYP.CCYCD
AND A03.CDVAL = TYP.APPRV_STS AND A03.CDTYPE = ''SY'' AND A03.CDNAME = ''APPRV_STS''','MRTYPE','frmMRTYPE',null,null,0,50,'N',30,null,'Y','T');
COMMIT;
