INSERT INTO SEARCH (SEARCHCODE,SEARCHTITLE,EN_SEARCHTITLE,SEARCHCMDSQL,OBJNAME,FRMNAME,ORDERBYCMDSQL,TLTXCD,CNTRECORD,ROWPERPAGE,AUTOSEARCH,INTERVAL,AUTHCODE,ROWLIMIT,CMDTYPE)
VALUES ('OD8832','Danh sách giao d?ch TPCP s? thanh toán trong ngày','Danh sách giao d?ch TPCP s? thanh toán trong ngày','
SELECT od.orderid, od.txdate, st.cleardate, iod.confirm_no confirmnumber, od.seacctno, od.execamt, od.feeacr, od.taxsellamt,
    cf.custodycd, od.afacctno, od.afacctno ciacctno, cf.fullname, sb.symbol, sb.codeid, iod.matchqtty orderqtty,
    iod.matchprice quoteprice, od.exectype, a1.cdcontent exectype1, sb.parvalue
FROM cfmast cf, afmast af, odmast od, stschd st, vw_iod_all iod, sbsecurities sb, allcode a1
WHERE cf.custid = af.custid AND af.acctno = od.afacctno
    AND od.orderid = st.orgorderid AND od.codeid = sb.codeid AND od.orderid = iod.orgorderid
    AND st.cleardate = getcurrdate AND sb.bondtype IN (''001'',''002'',''003'') AND sb.sectype = ''006''
    AND st.status <> ''C'' AND cf.custatcom = ''Y''
    AND od.errod = ''N'' AND od.matchtype = ''P''
    AND st.duetype IN (''RS'',''RM'')
    AND od.exectype IN (''NB'',''NS'',''MS'')
    AND od.exectype = a1.cdval AND a1.cdname = ''EXECTYPE'' AND a1.cdtype = ''OD''
','ODMAST',null,null,'8832',null,50,'N',30,'NYNNYYYNNN','Y','T');
COMMIT;
