VALUES ('OD8877','Hiển thị các lệnh nhóm','Hiển thị các lệnh nhóm','SELECT B.ORDERID ORDGRPID, REPLACE(SUBSTR(B.ORDERID,1,4) ||SUBSTR(REPLACE(TO_DATE(A.TXDATE,''DD/MM/RRRR''),''/'',''''),1,4) || SUBSTR(REPLACE(TO_DATE(A.TXDATE,''DD/MM/RRRR''),''/'',''''),7,2) || TO_CHAR(SEQ_ODMAST.NEXTVAL,''000000''),'' '','''')
   ORDERID, B.REFID SEACCTNO, B.TYPE, A.CODEID,C.SYMBOL , A.ACTYPE,A.QUOTEPRICE/F.TRADEUNIT QUOTEPRICE,  B.QTTY ORDERQTTY, A.AFACCTNO, A.CLEARDAY,  A.BRATIO, A.LIMITPRICE, C.PARVALUE,
    A.EXPDATE EFFDATE, A.TIMETYPE, A.EXPDATE, A.EXECTYPE, A.NORK, A.MATCHTYPE, A.VIA, A.CLEARCD, A.PRICETYPE,B.ORDERID VOUCHER, ''I'' CONSULTANT,
    A.AFACCTNO ||''.''||E.FULLNAME||''.''||A.MATCHTYPE||A.EXECTYPE||''.''||B.QTTY||''.''|| A.QUOTEPRICE/F.TRADEUNIT DESCRIPTION, A.CONTRAFIRM, A.TRADERID, A.CLIENTID,
    ''N'' OUTPRICEALLOW, '''' ADVIDREF, 0 FEEAMT, E.FULLNAME CUSTNAME,
    ''N'' GRPORDER, 0 ISMORTAGE, A.CONTRAFIRM CONTRACUS, ''3'' PUTTYPE, ''A'' CONTRAFIRMA, B.ORDERID SSAFACCTNO, B.ORDERID DEALID,1 TRADEUNITGTC, ''A'' MODEA, F.TRADEUNIT, 100 HUNDRED
FROM ODMAST A, (SELECT * FROM ODMAPEXT WHERE DELTD <> ''Y'' ORDER BY ORDERID) B, SBSECURITIES C, AFMAST D, CFMAST E , SECURITIES_INFO F
    WHERE A.ORDERID = B.ORDERID AND A.CODEID=C.CODEID AND D.ACCTNO=A.AFACCTNO AND D.CUSTID=E.CUSTID AND A.CODEID=F.CODEID','ODMAST','frmGROUPORDERPT',null,'8877',null,50,'N',30,null,'Y','T');
COMMIT;
