VALUES ('ODER02','Xóa đánh dấu lệnh lỗi giao dịch (8847)','Revert Mark error order (8847)','SELECT OD.TXDATE, OD.ORDERID, CF.CUSTODYCD, STS.AFACCTNO, SB.CODEID, SB.SYMBOL, OD.EXECTYPE EXECTYPEVL, A.CDCONTENT EXECTYPE,
    OD.ORDERQTTY, OD.PRICETYPE , OD.QUOTEPRICE, OD.EXECQTTY MATCHQTTY, OD.MATCHAMT, STS.CLEARDATE, OD.ERRREASON, CF.FULLNAME
FROM STSCHD STS, ODMAST OD, CFMAST CF, AFMAST AF, SBSECURITIES SB, ALLCODE A
WHERE STS.AFACCTNO = AF.ACCTNO AND CF.CUSTID = AF.CUSTID
    AND STS.CODEID = SB.CODEID AND STS.ORGORDERID = OD.ORDERID
    AND STS.DUETYPE IN (''RM'',''RS'') AND STS.DELTD = ''N''
    AND OD.ERROD = ''Y'' AND OD.ERRSTS = ''M''
    AND A.CDTYPE = ''OD'' AND A.CDNAME = ''EXECTYPE'' AND A.CDVAL = OD.EXECTYPE','ODMAST',null,'TXDATE DESC, CUSTODYCD, SYMBOL, ORDERID','8847',null,50,'N',30,'NYNNYYYNNN','Y','T');
COMMIT;
