VALUES ('ODMAST','Quản lý sổ lệnh','Order management','SELECT * FROM (
SELECT OD.*,
CASE WHEN NVL(TBL.ORDERID,''0'') <> ''0'' THEN ''Y'' ELSE ''N'' END  ISPTRP,
CASE WHEN NVL(TBL2.ORDERID,''0'') <> ''0'' THEN ''Y'' ELSE ''N'' END  ISPTRP2
FROM
VW_ODMAST OD, AFMAST AF,
(SELECT ORDERID FROM  TBL_ODREPO WHERE deltd=''N''
 UNION
 SELECT REF_ORDERID  ORDERID FROM TBL_ODREPO WHERE deltd=''N'' AND REF_ORDERID IS NOT NULL   ) TBL,
(SELECT ORDERID2  ORDERID FROM  TBL_ODREPO WHERE deltd=''N'' AND status =''A'' AND ORDERID2 IS NOT NULL
 union
 SELECT REF_ORDERID2 ORDERID FROM TBL_ODREPO WHERE DELTD=''N'' AND REF_ORDERID2 IS NOT NULL ) TBL2
WHERE OD.ORDERID = TBL.ORDERID(+)
AND OD.ORDERID = TBL2.ORDERID(+)
AND replace(OD.afacctno,''.'','''') = AF.acctno
AND AF.CAREBY IN (SELECT TLGRP.GRPID FROM TLGRPUSERS TLGRP WHERE TLID = ''<$TELLERID>'')) WHERE 0=0','ODMAST','frmODMAST','ORDERID DESC',null,null,50,'N',30,null,'Y','T');
COMMIT;
