FROM CRBTXREQ MST, AFMAST AF, CFMAST CF, AFTYPE TYP, CRBTRFLOG RF, CRBTRFLOGDTL DTL, ALLCODE A1
WHERE MST.STATUS IN (''A'',''S'') AND MST.TRFCODE=''TRFSEFEE''
