SELECT VSD.*, /*TO_CHAR(SE.imptype)*/ '''' CLASSIFY,REQ.MSGSTATUS TT, NVL(VSD.Afacctno,''0000000000'') Afacctno_CF FROM VW_VSDTXREQ VSD, VSDTXREQ REQ,sesendout se
