VALUES ('SE2201','Tra cứu các tài khoản xin rút chứng khoán','View pending to to Withdraw (wait for 2201)','SELECT   FN_GET_LOCATION(AF.BRID) LOCATION, SUBSTR(SEMAST.ACCTNO,1,4) || ''.'' || SUBSTR(SEMAST.ACCTNO,5,6) || ''.'' || SUBSTR(SEMAST.ACCTNO,11,6) ACCTNO, SYM.SYMBOL, SUBSTR(SEMAST.AFACCTNO,1,4) || ''.'' || SUBSTR(SEMAST.AFACCTNO,5,6) AFACCTNO,
SEWD.WITHDRAW  WITHDRAW,SEMAST.CODEID,SYM.PARVALUE,SEWD.PRICE  PRICE ,CF.FULLNAME, CF.IDCODE LICENSE, CF.IDDATE,CF.IDPLACE,CF.ADDRESS ,SEMAST.LASTDATE SELASTDATE,AF.LASTDATE AFLASTDATE, CONFIRMTXDATE LASTDATE,CF.CUSTODYCD,A1.CDCONTENT TRADEPLACE, SEWD.TXDATETXNUM, SEWD.TXDATE, SEWD.TXNUM,
SEMAST.AFACCTNO || NVL(SYM.REFCODEID,SYM.CODEID) ACCTNO_UPDATECOST
FROM SEMAST,SBSECURITIES SYM,AFMAST AF,CFMAST CF,ALLCODE A1
, SECURITIES_INFO SEINFO,  SEWITHDRAWDTL SEWD
WHERE SYM.CODEID=SEINFO.CODEID
AND A1.CDTYPE = ''SE'' AND A1.CDNAME = ''TRADEPLACE'' AND A1.CDVAL = SYM.TRADEPLACE
AND CF.CUSTID =AF.CUSTID
AND SYM.CODEID = SEMAST.CODEID
AND SEMAST.afacctno= AF.acctno
AND SEMAST.WITHDRAW >0
AND SEMAST.ACCTNO = SEWD.ACCTNO
AND SEWD.STATUS = ''A''','SEMAST',null,null,'2201',null,50,'N',30,'NYNNYYYNNN','Y','T');
COMMIT;
