VALUES ('V_SE2240','Danh sách import giao dịch lưu ký chứng khoán (2240)','View pending securities deposit (wait for 2240)','
SELECT TB.AUTOID,SB.CODEID, TB.SYMBOL, TB.CUSTODYCD,TB.AFACCTNO,TB.AFACCTNO||SB.CODEID  ACCTNO,
CF.FULLNAME CUSTNAME,CF.IDCODE LICENSE,CF.ADDRESS ADDRESS,CF.IDDATE LICENSEDATE,
AF.PHONE1 PHONE, SB.PARVALUE PARVALUE, TB.DEPOTRADE,TB.PRICE,TB.QTTYTYPE,
TB.CUSTNAMEAUTH, TB.DEPOBLOCK, TB.LICENSEAUTH, TB.WTRADE, TB.RDATE,
TB.DEPOTRADE+TB.DEPOBLOCK  QTTY, TB.LICENSEAUTHDATE,TB.CFACCTNOAUTH,TB.LICENSEAUTHPLACE, TB.TX_NUM,TB.DES,TB.REFERENCEID,
TB.CAQTTY011, TB.TAXRATE011, TB.CAQTTY021, TB.TAXRATE021
FROM TBLSE2240 TB, SBSECURITIES SB, CFMAST CF, AFMAST AF
WHERE TB.AFACCTNO = AF.ACCTNO AND TB.CUSTODYCD = CF.CUSTODYCD AND TB.SYMBOL = SB.SYMBOL
AND CF.CUSTID= AF.CUSTID AND NVL(TB.DELTD,''0'') <>''Y''  AND TB.AUTOID
NOT IN (SELECT REFKEY FROM TLLOGEXT WHERE TLTXCD=''2240'' AND DELTD=''N'' AND STATUS IN (''0'',''1'', ''3'',''4'') ) ','SEMAST','frmSEMAST',null,'2240',null,50,'N',30,'NYNNYYYNNN','Y','T');
COMMIT;
