VALUES ('V_SE2245','Danh sách import giao dịch nhận chuyển khoản chứng khoán (2245)','View pending securities tranfer  (wait for 2245)','
SELECT TB.autoid, ''VSDDEP'' FEETYPE, TB.CODEID ,TB.SYMBOL, TB.INWARD,
TB.CUSTODYCD,TB.AFACCTNO,TB.AFACCTNO||SB.CODEID  ACCTNO,CF.FULLNAME CUSTNAME,
CF.ADDRESS,CF.IDCODE LICENSE,TB.PRICE,TB.AMT,TB.DEPOBLOCK, TB.AMT+ TB.DEPOBLOCK QTTY,
SB.PARVALUE PARVALUE,TB.QTTYTYPE,TB.TRTYPE,TB.DEPOLASTDT,TB.DEPOFEEAMT,TB.DEPOFEEACR,TB.DES,
tb.caqtty011, tb.taxrate011, tb.caqtty021, tb.taxrate021
FROM TBLSE2245 TB, SBSECURITIES SB, CFMAST CF, AFMAST AF
WHERE TB.CUSTODYCD = CF.CUSTODYCD AND TB.AFACCTNO = AF.ACCTNO
AND CF.CUSTID= AF.CUSTID AND TB.SYMBOL = SB.SYMBOL AND NVL(TB.DELTD,''0'') <>''Y''  AND TB.AUTOID NOT IN (SELECT REFKEY FROM TLLOGEXT WHERE TLTXCD=''2245'' AND DELTD=''N'' AND STATUS IN (''0'',''1'', ''3'',''4'') )','SEMAST','frmSEMAST',null,'2245',null,50,'N',30,'NYNNYYYNNN','Y','T');
COMMIT;
